import React, { useState, useEffect } from 'react';

export default function SplashScreen({ onLaunch }) {
  const [dots, setDots] = useState([]);
  const [showBox, setShowBox] = useState(false);
  const [showText, setShowText] = useState(false);
  const [progress, setProgress] = useState(0);
  const [showButton, setShowButton] = useState(false);

  useEffect(() => {
    // Animate dots appearing
    const dotInterval = setInterval(() => {
      setDots(prev => {
        if (prev.length < 12) {
          return [...prev, prev.length];
        }
        clearInterval(dotInterval);
        return prev;
      });
    }, 150);

    // Show box after dots
    setTimeout(() => setShowBox(true), 2000);

    // Show text after box
    setTimeout(() => setShowText(true), 2500);

    // Start progress bar
    setTimeout(() => {
      const progressInterval = setInterval(() => {
        setProgress(prev => {
          if (prev >= 100) {
            clearInterval(progressInterval);
            setTimeout(() => setShowButton(true), 300);
            return 100;
          }
          return prev + 2;
        });
      }, 50);
    }, 3000);

    return () => clearInterval(dotInterval);
  }, []);

  return (
    <div 
      className="fixed inset-0 flex flex-col items-center justify-center"
      style={{
        backgroundImage: 'url(https://images.unsplash.com/photo-1758270703402-f09584bc2912?q=80&w=1631&auto=format&fit=crop)',
        backgroundSize: 'cover',
        backgroundPosition: 'center'
      }}
    >
      {/* Overlay */}
      <div className="absolute inset-0 bg-black/50"></div>

      {/* Content */}
      <div className="relative z-10 flex flex-col items-center">
        {/* Dots Container */}
        <div className="relative w-64 h-32 mb-8">
          {dots.map((dot, index) => (
            <div
              key={index}
              className="absolute w-4 h-4 bg-green-500 rounded-full animate-pulse"
              style={{
                left: `${(index % 4) * 25}%`,
                top: `${Math.floor(index / 4) * 33}%`,
                animation: `fadeIn 0.5s ease-in-out ${index * 0.1}s forwards`,
                opacity: 0
              }}
            />
          ))}
        </div>

        {/* Box with Text */}
        <div
          className={`transition-all duration-1000 ${
            showBox ? 'w-80 h-32 opacity-100' : 'w-0 h-0 opacity-0'
          } bg-green-600 rounded-lg flex items-center justify-center mb-8 overflow-hidden`}
        >
          {showText && (
            <h1 
              className="text-5xl font-bold text-white"
              style={{
                animation: 'typewriter 1s steps(8) forwards'
              }}
            >
              DevLearn
            </h1>
          )}
        </div>

        {/* Progress Bar */}
        <div className="w-80 mb-8">
          <div className="w-full bg-gray-700/50 rounded-full h-4 overflow-hidden backdrop-blur-sm">
            <div
              className="h-full bg-green-500 transition-all duration-300 flex items-center justify-center"
              style={{ width: `${progress}%` }}
            >
              <span className="text-xs font-bold text-white">{progress}%</span>
            </div>
          </div>
        </div>

        {/* Launch Button */}
        {showButton && (
          <button
            onClick={onLaunch}
            className="px-8 py-4 bg-green-600 hover:bg-green-700 text-white text-xl font-bold rounded-lg transition transform hover:scale-105 shadow-2xl"
            style={{
              animation: 'popIn 0.5s ease-out forwards'
            }}
          >
            Launch DevLearn
          </button>
        )}
      </div>

      <style jsx>{`
        @keyframes fadeIn {
          to {
            opacity: 1;
          }
        }

        @keyframes typewriter {
          from {
            width: 0;
          }
          to {
            width: 100%;
          }
        }

        @keyframes popIn {
          0% {
            transform: scale(0);
            opacity: 0;
          }
          50% {
            transform: scale(1.1);
          }
          100% {
            transform: scale(1);
            opacity: 1;
          }
        }
      `}</style>
    </div>
  );
}
